import React, { useEffect } from 'react';
import { ChatWrapper } from './style.js';
import {HubConnectionBuilder} from '@microsoft/signalr';

const Chat = () => {

  const ChatImplementation = () => {
    var connection = new HubConnectionBuilder().withUrl("/chatHub").build();

    //Disable send button until connection is established
    document.getElementById("sendButton").disabled = true;

    connection.on("ReceiveMessage", function (user, message) {
      var msg = message.replace(/&/g, "&amp;").replace(/</g, "&lt;").replace(/>/g, "&gt;");
      var encodedMsg = user + " says " + msg;
      var li = document.createElement("li");
      li.textContent = encodedMsg;
      document.getElementById("messagesList").appendChild(li);
    });

    connection.start().then(function () {
      document.getElementById("sendButton").disabled = false;
    }).catch(function (err) {
      return console.error(err.toString());
    });

    document.getElementById("sendButton").addEventListener("click", function (event) {
      console.log("clicou")
      var user = "user"//document.getElementById("userInput").value;
      var message = "message"//document.getElementById("messageInput").value;
      connection.invoke("SendMessage", user, message).catch(function (err) {
        return console.error(err.toString());
      });
      event.preventDefault();
    });

  }

  useEffect(()=>{
    ChatImplementation()
  })

  return (
    <ChatWrapper className="chat">
      <p>test</p>
      <ul id="ReceiveMessage"></ul>
      <button id="sendButton">Enviar</button>
    </ChatWrapper>
  );
};
export default Chat;
