import styled from 'styled-components'

export const BannerSection = styled.section`
    display:flex;
    margin-top:10vh;
    height:50vh; 
    flex-direction:column;
    justify-content:center;   
    align-items:center;
    padding:5%;
`

export const BannerH1 = styled.h1`
    font-size:3rem;
    text-align:center;
`

export const BannerH2 = styled.h2`
    text-align:center;
    font-size:2rem;
`
